import React from 'react'
import { createContext } from 'react';
import { useState } from 'react';
import Navbar from './Navbar';
const quantity=createContext();
export default function Home() {
   
    const [data,setData]=useState(0);

    
    function setter(){
      setData(data+1);
    }

    
    function remove(){
        if(data!=0){
        setData(data-1);}
    }

    function remove1(){
        if(data!=0){  //pahle cart me negative usestate ka value chala ja raha tha isliye if lagaya
        setData(data-1);}
    }
    

  return (
    <div>

<quantity.Provider value={data}>

<Navbar/>
</quantity.Provider>
      
{/**grids taken and cards put inside columns */}
{/**grid for mens clothing section */}
      <div class="container text-center">
  <div class="row">

    <div class="col">
        <p>Black and grey jacket</p><br></br>
       
      <img src="blackcloth.jpg" className='img-thumbnail' style={{height:'20rem'}}/><br></br>
      <p> price:150</p>
      <button className='btn btn-primary' onClick={setter}>Add To Cart</button>
      <button className='btn btn-danger' onClick={remove}>Remove</button>
    </div>

    <div class="col">
        <p>Formal black dress for office</p>
      <img src='formal men.jpg' className='img-thumbnail' style={{height:'20rem'}}/>
      <button className='btn btn-primary'  onClick={setter}>Add To Cart</button>
      <button className='btn btn-danger' onClick={remove1}>Remove</button>
    </div>

    <div class="col">
        <p>Aggressor Flex - Tactical Pants </p>
      <img src='black pant.jpg' className='img-thumbnail' style={{height:'20rem'}}></img>
      <button className='btn btn-primary'  onClick={setter}>Add To Cart</button>
      <button className='btn btn-danger' >Remove</button>
    </div>

    <div class="col">
        <p>Black and white Combo</p>
      <img src='white and black.jpg' className='img-thumbnail' style={{height:'20rem'}}></img>
      <button className='btn btn-primary'  onClick={setter}>Add To Cart</button>
      <button className='btn btn-danger' >Remove</button>
    </div>

  </div>
</div>
{/**grids for mens clothing section end here */}


    </div>
  )
}

export {quantity}
